mapper.select:
SELECT count(*)
FROM opm_assets_info
WHERE hasDelete = '0'
AND code = #{assetsCode}
AND categoryCode in

    #{item}

select count(*)
from
( select f_guid,assets_code from bes_ammeter
  union
  select f_guid,assets_code from opm_ammeter_delete
) t
where assets_code = #{assetsCode}

    and f_guid != #{fGuid}

select count(*) from seb_point

    and F_SCREEN_CODE = #{screenCode}

        and F_GUID != #{fGuid}
