mapper.select:
SELECT B.F_GUID, B.F_GNBH , B.F_GNMC , B.F_CSS_CLASS, B.F_URL , B.F_HIDE, B.F_MKGUID ,B.F_SORT, B.F_JS , B.F_MX , B.F_PGNBH, B.F_FULLSCREEN_TYPE, B.F_OPEN_TYPE,B.F_TABCLOSEABLE, B.F_PARAMS FROM ES_GNZD B LEFT JOIN ${tablename} A ON A.F_GNZDID = B.F_GNBH
F_MKGUID = #{mkbh} AND A.F_SH = "0" AND A.F_YHBH = #{fYhbh}
order by F_SORT asc

SELECT B.F_GUID, B.F_GNBH , B.F_GNMC , B.F_CSS_CLASS, B.F_URL , B.F_HIDE, B.F_MKGUID ,B.F_SORT, B.F_JS , B.F_MX , B.F_PGNBH, B.F_FULLSCREEN_TYPE, B.F_OPEN_TYPE,B.F_TABCLOSEABLE, B.F_PARAMS FROM ES_GNZD B LEFT JOIN ${tablename} A ON A.F_GNZDID = B.F_GNBH
F_MKGUID = #{mkbh} AND A.F_SH = "0" AND A.F_ROLEBH = #{fRolebh}
order by F_SORT asc

mapper.update:
UPDATE es_gnzd

	F_MX = #{mx}

WHERE F_GNBH=#{gnbh} and F_MKGUID=#{mkbh}

